# cette fonction compte combien il y a d'éléments dans l'array "handles"
def combien?(array)
  compte = array.count  
  puts compte
end 
# cette fonction donne l'élément le plus court au sein de l'array "handles"
def short(array)
  # la fonction .min_by va soutirer l'élément le plus petit d'après une métrique que l'on doit indiquer
  # on décide d'indiquer que la métrique utilisée sera celle obtenue par la fonction length et on la puts 
  shortest = array.min_by(&:length)
  puts shortest
end 
# cette fonction indique le nombre d'éléments dans "handles" qui contiennent 5 caractères, en excluant le @ 
def cinq?(array)
  cinq = array.select {|n| n.length == 6}
  puts cinq.count    
end 
# cette fonction vise à indiquer le nombre d'éléments qui commence par une majuscule en excluant le @ 
def maj?(array)
  # ici on a utilisé une RegEx
  # /[A-Z]/ liste les majuscules 
  # =~ signifie "qui contient au moins l'un"
  maj = array.select {|n| n[1] =~ /[A-Z]/} 
  puts maj.count
end
# cette fonction vise à trier les éléments par ordre alphabétique 
def alpha(array) 
  puts array.sort_by {|a| a.downcase} 
end 
# cette fonction trie les éléments du plus petit au plus grand (nombre de lettres)
def taille(array) 
  puts array.sort_by(&:length)
end
# cette fonction donne la position (index) du string "@epenser" 
def position(array)
  posit = array.each_index.select{|n| array[n] == "@epenser"}
  puts posit 
end 
# cette fonction vise à répartir les éléments de l'array par longueur 
def repart(array) 
  longest = array.max_by(&:length)
  longnum = longest.length  
  for iter in 1..longnum 
    liste = array.select {|n| n.length == iter}
    if liste.count > 0 
      puts "Voici les handles dont la longueur est de #{iter} caractères"  
      puts liste 
    else
    end 
  end 
end 
